import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o,c as s,a as e,f as _}from"./app-53ceca5a.js";const c={},n=e("h1",{id:"汉王兴兵",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#汉王兴兵","aria-hidden":"true"},"#"),_(" 汉王兴兵")],-1),a=e("p",null,"乱世末，齐王扫清江北，南下灭赵，践祚为帝，号齐高祖。齐高祖宽厚爱人，时人称之。天下既定，封其重臣于衡，曰衡王。衡王者，礼部重臣也，励精图治，兴庠序之教，化诗礼之风，江北秀才贤士，摩肩接踵。齐传五世，至齐景帝，贪图声色，国力渐衰，乃仿汉武之制，兴推恩之举以备不虞，故依形胜削衡为二，北者曰北衡，南者曰南衡。景帝三年，南衡为泰所灭。",-1),r=e("p",null,"景帝六年，衡大将军石惠勾连泰君，胁衡王禅让，是为衡哀王。及即位，搜刮财赀，时人皆怨愤。泰君者，亦好利之徒也，自是两国国力渐衰。",-1),d=e("p",null,"齐景帝十年，冀北，辽北，华东大疫，帝时为布衣，学于泰。泰君惧疫，锁国自守，唯缙绅得以出入，更设厂卫，日夜纠察，少有犯禁，辄论弃市。逾数月，国中衣少食尽，诸生病之也久。是岁西元三月廿七夜，帝谓同舍生曰：“泰君倒行逆施，不若作困兽之斗，何如？”同舍生皆愤然嗔目而然之。同舍生者，张理琦、梁伏希、李辰风之属也。是夜，与密谋反策，定起义计。帝及左右暗遁至各舍馆，诸生莫不慨然，期子时举火为号，共举大计。亥时三刻，帝方与左右谋，忽有厂卫暗窥觉知，破门入，众人惊，唯帝泰然自若，未及厂卫言语，帝攻其不备，以书册击其首，众见状，又数击毙之。共计曰：“此时不反，却待何时！”即刻举火为号，喊声雷动，电光石火之间，所至之处，莫不云集响应。挟书册、铁器以从者，不可胜计。帝亲携众搜罗泰之文书诏令，官服品饰，举火焚之，以示其反。泰君在大内，闻之而惊，会将兵者皆出于外，非有诏不得入，而帝率义军攻之甚急，亟命其黄门侍郎马忠幄率内官及禁军出战。帝遣荆子川出战，战不三合，忠幄力怯遁走，子川逐之，斩其首。帝挥旌掩杀，克城而执泰君。帝数之曰：“尔为国主，不能行仁政，惜民力，但争赀财之利，贪禽色之乐，吾等兴义兵，正为恭行天罚也！”命斩泰君首，弃市十日。夜修守备，开府库以赈斯民。乃自为王，改国号为汉。汉王乃命张理琦、李辰风、梁伏希率众拒守，以备泰之旧臣。旦日，果有泰旧部率众勤王。汉王乃发诏令，示之以泰君首，遗臣愤怒，礼部尚书刘文杰尤为主战。当是时，禁军教头刘子龙自荐出战迎敌。张理琦佯败走，梁伏希、李辰风断泰军后，困之，子龙乃降。泰军既降，以告遗臣，或欲守节，或欲降者。汉王乃尽收其泰遗臣欲降者。",-1),l=[n,a,r,d];function i(h,f){return o(),s("div",null,l)}const p=t(c,[["render",i],["__file","0.html.vue"]]);export{p as default};
