import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o,c as s,a as e,f as _}from"./app-53ceca5a.js";const c={},n=e("h1",{id:"荆子川传",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#荆子川传","aria-hidden":"true"},"#"),_(" 荆子川传")],-1),a=e("p",null,"荆子川，冀州开平人。少有才学，以诗文行于世，喜言阴阳之术。时参知政事张理琦奇之，举为翰林待诏，累迁至著作郎，兼国史修撰。建平四年，从上郊祭，子川上表四篇以砭时弊，深切帝心。上悦之，诏擢左谏议大夫。先是，参知政事张理琦举子川，及子川为著作郎，常与琦同游，又尝造淮水，诣镜湖，吟咏不辍，痛饮放歌而还，时人以为逸士，世并称“荆琦”者云。",-1),r=e("p",null,"十五年，出为北地按察使，领景州刺史。时宋慧真反，兵数万寇慕州，守者莫能当。子川请驰往救之，许之。遂将景州兵八千趣慕州，破贼先锋吴忠友，生得之。钟友降，子川亲释其缚，使为左前锋，钟友德之。抵城下，屯安平里，令法《周易》数作混元八门阵。慧真以军士众，不以为意。及战，慧真军士入者皆困死阵中，众大骇，以为神。子川复使吴忠友通其旧部曲，散言曰：“无从俱死也！”宋慧真军大溃，乃解围走。子川待慕州军与俱，驰往击之，大破之，杀伤甚众，慧真出奔海上。捷报京师，帝顾左右，抚掌而喜曰:“不期文墨之士得建此奇功！”进封靖安侯，邑千户，位特进。",-1),d=e("p",null,"子川身长八尺，而体态壮硕，因好饮食，就席饭斗米，肉数斤方足，犹自嘲曰：“余食粟米如饮水浆，安复有足乎！”又好美姬，惟不致乱耳。又文武兼备，居家常弄文墨，师承怀颜，真草相半；犹擅属文赋诗，辞藻甚工，常赋诗自娱，信手为之，卒为奇绝，好事者录之，有文集行于世。景宪六年薨，赠镇远将军，光禄卿，谥曰明。",-1),l=[n,a,r,d];function i(h,f){return o(),s("div",null,l)}const p=t(c,[["render",i],["__file","0.html.vue"]]);export{p as default};
