import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o,c as s,a as e,f as n}from"./app-53ceca5a.js";const _={},a=e("h1",{id:"宋慧真传",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#宋慧真传","aria-hidden":"true"},"#"),n(" 宋慧真传")],-1),c=e("p",null,"宋慧真，冀州真定人，少以父任，除安西防御使，累征北地，数建功勋，擢征虏大将军。英宗践祚，慧真趣京拜贺。时张理琦为参知政事，时欲制其兵。慧真衔之，常作诗以讽。建平六年，功加擢平远大将军，进封明信侯，赐绯衣，金百镒，位特进。",-1),r=e("p",null,"明宗时，慧真与琅琊王镇素厚，尝请立镇为太子。明宗亦私镇。朝议力谏不许，遂罢。当是时，宋氏势大，慧真因有改立镇为帝之意。因与吴钟友谋之。吴钟友者，骠骑将军吴钟升之弟也，与慧真素厚，然常修谨以自戒。及问，钟友曰：“共立明主，天下幸甚。然立嫡以长，不宜更易。”慧真弗听，阴募军士，治革马。建平十五年，英宗巡守南境，慧真乃尽起兵北地，携琅琊王镇南进，拔数州，朝野震骇。时参知政事张理琦亟遣兵驰援，慧真断其漕，兵饷不至，殆几战败。北地按察使荆子川领景州兵趣往救之，降吴钟友，因出奇谋大破慧真军。慧真解慕州围走，子川及理琦兵复围之。慧真携镇，将心腹夜溃围出，走渤海上。官军不习水舟，遂罢。馀者皆降。",-1),i=e("div",{style:{"text-align":"right"}},[e("p",null,"（节选自《衡史·宋慧真传》，有改动）")],-1),l=[a,c,r,i];function d(h,f){return o(),s("div",null,l)}const p=t(_,[["render",d],["__file","2.html.vue"]]);export{p as default};
