import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o,c as s,a as e,f as _}from"./app-53ceca5a.js";const n={},c=e("h1",{id:"楚君雒传",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#楚君雒传","aria-hidden":"true"},"#"),_(" 楚君雒传")],-1),a=e("p",null,"楚君雒，直隶碣石人。本张氏，父尝为涿州令，以清廉闻。少家贫，尤好读诸子百家之书，喜纵横之言，兵家之术，博闻强识，山川地理，无不毕晓。年七岁，与乡人子游戏，取藤枝画地语曰：“他日裂土而封！”年十四，从父诣京师，时杨朱华方用事，君雒谒其府，自云有言于杨公，门人叱之，固请。朱华闻之，出见其眉宇不凡，喝止门人，以礼请内，见之东阁。君雒指墙上北地之图曰：“鞑靼方聚，狐岭要冲，京师咽喉，失之则国亡矣。公独不见元克金之故事乎？”朱华亟召左右问之，乃知狐岭守备原固，微朔二年，帝以其地狭险，裁调守备，今已空虚矣。朱华趣阙疏奏，着调山东兵士十万以备边事。未二年，鞑酋果率众经狐岭入寇，微君雒言，国事几殆。帝召朱华嘉之，朱华具以君雒事奏。帝大奇之，趣召君雒入对，言军国机要，靡不以奇策毕奏，应答如流。帝大喜，然以其年方幼，诏赐龙泉剑一口，曰：“他日当为国之锋刃也！”时年方十六，朝野异之。",-1),l=e("p",null,"微朔十二年，以状元及第，入朝面圣。上孰视之，笑曰：“子非吾之刃乎？”对曰：“进则为刃，退则为盾。”上大喜，授兵部给事中。未几，兵部侍郎乌子虚因疾乞骸骨，上许之，遂以君雒补阙。时年方弱冠，处事已有种师道、俞大猷之风。每朝必有奏，奏则必切，切则必中，中则必纳，上谑之曰“谏部尚书”，君雒欣然以之自谓。未几，荆楚大疫，瘴自蜀来，缕缕不绝；毒由沼滋，生生不息。楚民病之日久，妻子离散，颠沛流离者甚众。荆楚总督卒于任，闻之庙堂，朝野大骇。上忧之。君雒请往，上以兵戈之事不类此，兼惧其往而殁，不许。君雒凛然曰：“自臣蒙国恩，忝居要职，未尝得寸功以立；今楚有难，国有殇，吾往矣，更胜沙场点兵。愿陛下听臣微志，以全赤胆！”上喟然曰：“子真吾坚盾也！”加太子少保，领工部侍郎，持节往。即行，上亲进二百里送之。及别，上潸然若儿女状，曰：“待卿得胜，朕亦当往迎之！”君雒再稽以谢。",-1),r=e("p",null,"微朔十四年，荆楚疫销，而君雒无讯。上寤寐思之，欲以国礼立衣冠冢，朱华以尸首未见，卒讯未明，固辞，请往视。上以朱华居相要，不许，乃遣山东巡抚宋慧真。及至，遍访深山，得荆水之鄙有村，男丁殆尽，唯余老幼，君雒躬耕亲养。及慧真至，方知“张小北”乃朝廷命官，欲行大礼。君雒抢伏以谢，泣曰：“罪臣不能全诸家，万死亦有余辜，何敢当此大礼！”固请留养。慧真晓之以上思切，由是诀别，将行以百金相遗，乃君雒功之赏也。妇孺夹道相送，山呼“楚公”，君雒感之，遂以楚为姓。",-1),d=e("p",null,"及还，圣驾三百里相迎，还朝之日，上斋沐一日以旌，天下称之。",-1),i=e("p",null,"建平元年，李岳松伏诛；建平十年，宋慧真反。越明年，渐有抗礼之势。上欲遣君雒往平之。君雒感其节义，又以慧真因岳松冤而反，挂印而去。待参知政事张理琦趋往视之，四室已俱空矣。",-1),h=[c,a,l,r,d,i];function u(f,p){return o(),s("div",null,h)}const B=t(n,[["render",u],["__file","3.html.vue"]]);export{B as default};
