import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o,c as s,a as e,f as _}from"./app-53ceca5a.js";const n={},c=e("h1",{id:"白水",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#白水","aria-hidden":"true"},"#"),_(" 白水")],-1),l=e("p",null,"张理琦与荆子川共同在寒食节外出游玩。荆子川穿着华丽的带有装饰的衣服，乘坐着高大的马所拉的马车，在约定的地点等候。过了一会儿，张理琦才到。张理琦穿着粗布制成的衣服，独自一个人步行。",-1),a=e("p",null,"荆子川对此感到很惊讶，说：“很奇怪呀，你的行为！拥有绸缎却只穿葛布制成的衣服，拥有华丽的马车却甘愿步行，拥有成群的侍从却独自一人出行。有意使自己的外表和行为不呵护自己的身份，这也能算作君子吗？”",-1),d=e("p",null,"张理琦笑而不言，请荆子川先出发，约定在白水畔见面。荆子川虽然感到惊讶困惑，但还是听从了。直到中午张理琦才赶到。",-1),r=e("p",null,"荆子川说：“美好啊，这段旅程！游玩的百姓看到我的车马纷纷避让，知道是显贵之人游春，我因此能够畅通无阻，欣赏到自然雄伟的景色。”",-1),i=e("p",null,"张理琦说：“我出游的时候，百姓不知道我的身份，都把我当作普通的年轻男子对待，因此我虽然不曾穷尽游览山水的乐趣，却得以感受普通人的快乐，不也很美好吗？”",-1),h=e("p",null,"荆子川笑着说：“好啊，你的看法！保有世俗却得以脱离世俗，抛弃世俗却得以感受世俗之美，说的不正是这个道理吗？”",-1),u=e("p",null,"张理琦说：“你的言论没有错。但对于得道的人来说，保有世俗和脱离世俗真的有什么区别吗？依照至道办事的人，他的行为遵从自然而又能体现他的内心，他的做法别具一格而又在情理之中，他的举止超凡脱俗而又富有情味，他的心灵空明澄澈而又丰富充实。认为只有与尘世决裂，斋戒修持，做尽了这些表面功夫才能得道的人，往往不明白这其中的至理，他们是多么荒谬啊！依照至道生活的人，往往时而超然物外，时而谨小慎微；时而精妙智慧，时而荒诞不羁；时而勇敢无畏，时而明哲保身；这都不过是依照道的运动而变化罢了。今日你我的快乐，虽是不同的快乐，却不也是相同的快乐吗！”",-1),p=[c,l,a,d,r,i,h,u];function f(m,x){return o(),s("div",null,p)}const v=t(n,[["render",f],["__file","6.html.vue"]]);export{v as default};
