import{_ as s}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as a,c as d,a as o,b as l,w as n,f as t,d as i}from"./app-53ceca5a.js";const c={},m=o("h2",{id:"目录",tabindex:"-1"},[o("a",{class:"header-anchor",href:"#目录","aria-hidden":"true"},"#"),t(" 目录")],-1),p=i('<hr><h3 id="【前言】" tabindex="-1"><a class="header-anchor" href="#【前言】" aria-hidden="true">#</a> 【前言】</h3><p><strong>《衡史》并不是一部真正的史书</strong>，这些历史性散文源自于高三上学期流行于我、荆子川、李辰风、楚君雒等人之间的故事。当时我们正在进行文言文一轮复习，经常进行文言文阅读训练。突然有一天，我们想要仿照古代史书写一篇人物传记。</p><p>高二的时候，我和荆子川之间经常会用一种独特的方式娱乐：采用文言文翻译腔，写我们两个人的<strong>同人虚构故事</strong>。《衡史》的诞生，也是基于这些故事之上。</p><p>很快，《衡史》这种新颖的创作风格就流行于关系较好的几位同学间，并通过不断互相完善，形成了<strong>较为完整的时空体系</strong>，甚至仿照当时经常练习的历史题，衍生出了基于《衡史》的考据学和历史学。</p><p><strong>《衡史》包含多篇历史性散文，并非由我一人所写，而是多人所写，其中笔法令人钦佩</strong>。内容基本都是人物传记，但能够进行相互联动，让读者形成基本完善的时空观念。</p><p><strong>《衡史》目前已经重启更新计划</strong>。通过这些文字，<strong>可以一窥当年同学们之间真挚而不失诙谐的情谊</strong>。</p><p><em>注：为了保护个人隐私，对于内容中出现的名字做了一些删改。</em></p>',8);function h(u,_){const e=r("RouterLink");return a(),d("div",null,[m,o("ul",null,[o("li",null,[l(e,{to:"/poems/book2/5/1/0.html"},{default:n(()=>[t("荆子川传")]),_:1})]),o("li",null,[l(e,{to:"/poems/book2/5/1/1.html"},{default:n(()=>[t("张理琦传")]),_:1})]),o("li",null,[l(e,{to:"/poems/book2/5/1/2.html"},{default:n(()=>[t("宋慧真传")]),_:1})]),o("li",null,[l(e,{to:"/poems/book2/5/1/3.html"},{default:n(()=>[t("楚君雒传")]),_:1})]),o("li",null,[l(e,{to:"/poems/book2/5/1/4.html"},{default:n(()=>[t("李岳松传")]),_:1})]),o("li",null,[l(e,{to:"/poems/book2/5/1/5.html"},{default:n(()=>[t("论李岳松之死的历史真相")]),_:1})]),o("li",null,[l(e,{to:"/poems/book2/5/1/6.html"},{default:n(()=>[t("《衡史》后记")]),_:1})])]),p])}const k=s(c,[["render",h],["__file","index.html.vue"]]);export{k as default};
